$(document).ready(function() {
    $(".block").addClass("card");
	$(".button").addClass("waves-effect waves-light btn");
    $(".action").addClass("btn waves-effect waves-light red accent-2");
    $(".textArea").addClass("materialize-textarea");
    //$(".select-wrapper").addClass("col s12 m4 l4 lg4");
    $(".col-md-6").addClass("col m6");
    $('.modal').modal();
    $('tabs').tabs();
    $('.carousel.carousel-slider').carousel({
        fullWidth: true,
        indicators: true
    });
	$('.indexers.carousel.carousel-slider').carousel({
        fullWidth: true,
        indicators: true,
		numVisible: 5,
    });
    var autoplay = true;
    setInterval(function() { if(autoplay) $('.carousel.carousel-slider').carousel('next'); }, 4500);
    $('.carousel.carousel-slider').hover(function(){ autoplay = false; },function(){ autoplay = true; });
	$('.indexers .owl-carousel').owlCarousel(
    {
      items: 25,
      margin: 16,
      stagePadding: 32,
      loop: true,
      autoplay: true,
      autoplaySpeed: 500,
      navSpeed: 500,
      dots: false,
      dotsEach: true,
      nav: true,
      // navText: ['Back','Next'],
      navText: ['',''],
      autoplayTimeout: 5000,
      autoplayHoverPause: true,
      animateOut: 'slideOutDown',
      animateIn: 'flipInX',
	  responsiveClass:true,
	  responsive:{
        0:{
            items:2,
            nav:false
        },
        600:{
            items:4,
            nav:false
        },
        1000:{
            items:6,
            nav:true,
            loop:true
        }
    }
    }
  );
	$('[data-toggle="popover"]').popover();
	$('.tooltipped').tooltip();
	$('.fixed-action-btn').floatingActionButton();
	$('.scrollspy').scrollSpy({scrollOffset: 130});	
});